var page = 0;
var upperLimit = 4;

function moveItemsUp(){
	if(page == 0){
		return;
	}
	_buy.forEach(function(item){
		item.y+= 514;
	});
	page--;
}

function moveItemsDown(){
	if(page == upperLimit){
		return;
	}
	_buy.forEach(function(item){
		item.y-= 514;
	});
	page++;
}

